  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <button
          title={`카운트 변경`}
          onClick={() => {
            setCount(100);
          }}
        >
          count
        </button>
        <button
          onClick={() => {
            onPressButton2("15", setBalance);
          }}
        >
          hi
        </button>
        <p>{balance}</p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}

export default App;
